CREATE TABLE IF NOT EXISTS `cms_article` (
  `article_id` int(11) NOT NULL AUTO_INCREMENT,
  `article_title` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `article_alias` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `category_id` int(11) NOT NULL DEFAULT '0',
  `article_thumbnail` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,
  `article_template` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL,
  `article_author` int(11) NOT NULL DEFAULT '0',
  `article_intro` text COLLATE utf8_unicode_ci NOT NULL,
  `article_content` text COLLATE utf8_unicode_ci NOT NULL,
  `article_meta` text COLLATE utf8_unicode_ci,
  `article_status` varchar(20) COLLATE utf8_unicode_ci NOT NULL,
  `article_check_comment` int(1) NOT NULL DEFAULT '0',
  `article_date_created` int(11) NOT NULL,
  `article_date_updated` int(11) NOT NULL,
  PRIMARY KEY (`article_id`),
  UNIQUE KEY `post_slug` (`article_alias`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=11242 ;

