--
-- Table structure for table `cms_product_category`
--

CREATE TABLE IF NOT EXISTS `cms_product_category` (
  `product_cate_id` int(11) NOT NULL AUTO_INCREMENT,
  `product_cate_title` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `product_cate_alias` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `product_cate_meta` text COLLATE utf8_unicode_ci NOT NULL,
  `product_cate_parent` int(11) NOT NULL,
  PRIMARY KEY (`product_cate_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=27 ;

